```kotlin:app/src/main/java/com/iftikar/mediuser/presentation/screens/login_screen/LoginViewModel.kt, lines=ALL(1-40)
package com.iftikar.mediuser.presentation.screens.login_screen

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.iftikar.mediuser.domain.usecase.LoginUseCase
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.update
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class LoginViewModel @Inject constructor(private val loginUseCase: LoginUseCase) : ViewModel() {
 private val _loginState = MutableStateFlow<LoginState>(LoginState())
 val loginState = _loginState.asStateFlow()

 fun login(email: String, password: String) {
 viewModelScope.launch {
 _loginState.value = _loginState.value.copy(isLoading = true)
 loginUseCase(email = email, password = password).collect { apiOperation ->

 apiOperation.onSuccess {
 _loginState.value = _loginState.value.copy(
 isLoading = false,
 loginResponse = it,
 failure = null
 )
 }.onFailure {
 _loginState.value = _loginState.value.copy(
 isLoading = false,
 loginResponse = null,
 failure = it
 )
 }
 }
 }
 }
}
```
