import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.platform.LocalContext
import androidx.compose.ui.text.input.PasswordVisualTransformation
import androidx.compose.ui.text.input.VisualTransformation
import androidx.compose.ui.unit.dp
import androidx.hilt.navigation.compose.hiltViewModel
import androidx.lifecycle.compose.collectAsStateWithLifecycle
import androidx.navigation.NavHostController
import com.airbnb.lottie.compose.LottieAnimation
import com.airbnb.lottie.compose.LottieCompositionSpec
import com.airbnb.lottie.compose.LottieConstants
import com.airbnb.lottie.compose.animateLottieCompositionAsState
import com.airbnb.lottie.compose.rememberLottieComposition
import com.iftikar.mediuser.R
import com.iftikar.mediuser.data.remote.ApiOperation
import com.iftikar.mediuser.navigation.Routes
import com.iftikar.mediuser.presentation.components.textfield.TextFieldComponent

@Composable
fun LoginScreen(
 loginViewModel: LoginViewModel = hiltViewModel(),
 navHostController: NavHostController
) {
 val state = loginViewModel.loginState.collectAsStateWithLifecycle()
 var email by remember { mutableStateOf("") }
 var password by remember { mutableStateOf("") }
 var buttonText by remember { mutableStateOf("") }

 var isPasswordVisible by remember { mutableStateOf(false) }
 var emailError by remember { mutableStateOf("") }
 var passwordError by remember { mutableStateOf("") }

 val composition by rememberLottieComposition(LottieCompositionSpec.RawRes(R.raw.login_animation))

 val progress by animateLottieCompositionAsState(
 isPlaying = true,
 composition = composition,
 iterations = LottieConstants.IterateForever,
 speed = 0.6f
 )


 Scaffold(modifier = Modifier.fillMaxSize()) { innerPadding ->
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(innerPadding),

 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 LottieAnimation(
 modifier = Modifier
 .size(200.dp)
 .align(Alignment.CenterHorizontally),
 composition = composition,
 progress = { progress }
 )

 TextFieldComponent(
 value = email,
 onTextChange = { email = it },
 title = emailError.ifBlank() { "Email" },
 color = if (emailError.isNotEmpty()) Color.Red else Color.Transparent,
 leadingIcon = {
 Icon(
 imageVector = Icons.Outlined.Email,
 contentDescription = null
 )
 }
 )

 Spacer(Modifier.height(10.dp))

 TextFieldComponent(
 value = password,
 onTextChange = {password = it},
 title = passwordError.ifEmpty { "password" },
 visualTransformation = if (isPasswordVisible) VisualTransformation.None else PasswordVisualTransformation(),
 color = if (passwordError.isNotEmpty()) Color.Red else Color.Transparent,
 leadingIcon = {
 Icon(
 imageVector = Icons.Default.Password,
 contentDescription = null
 )
 },
 trailingIcon = {
 val icon = if (isPasswordVisible) Icons.Default.VisibilityOff else Icons.Default.Visibility
 IconButton(
 onClick = {isPasswordVisible = !isPasswordVisible}
 ) {
 Icon(
 imageVector = icon,
 contentDescription = null
 )
 }
 }
 )

 Button(
 onClick = {
 loginViewModel.login(email, password)
 }
 ) {
 Text(buttonText)
 }

 when (val viewState = state.value) {
 is LoginState.Error -> {
 Text(viewState.message)
 buttonText = "try again"
 }

 LoginState.Idle -> {
 buttonText = "log in"
 }

 LoginState.Loading -> {
 buttonText = "logging in"
 }

 is LoginState.Success -> {
 LaunchedEffect(Unit) {
 navHostController.navigate(Routes.WaitingScreen) {
 popUpTo(Routes.LoginScreen) { inclusive = true }
 }
 }
 }
 }
 }


 }
}























